export default {
  name: 'signupPage',
  data: function data() {
    return {
      name: 'Nom',
      firstName: 'Prénom',
      mail: 'Adresse mail',
      password: 'Mot de passe',
      birthday: 'Date de naissance',
      city: 'Ville',
      adress: 'Adresse',
      affichMdp: 'Aperçu du mot de passe',
      messageMdp: '',
      boolAffichMsg: false,
      numberAffichMsg: 0,
      regexpAdress: false,
      lastAndFirstNameRegExp: new RegExp("^[a-zA-Z\xE0\xE2\xE4\xE9\xE8\xEA\xEB\xEF\xEE\xF4\xF6\xF9\xFB\xFC\xE7\xB0 -]{1,}$"),
      regexAddress: new RegExp(/^[a-zA-Z0-9 ]+$/),
      cityRegExp: new RegExp("^[a-zA-Z\xE0\xE2\xE4\xE9\xE8\xEA\xEB\xEF\xEE\xF4\xF6\xF9\xFB\xFC\xE7\xB0 -]{1,}$"),
      emailRegExp: new RegExp("^[a-zA-Z0-9.-_]+[@]{1}[a-zA-Z0-9.-_]+[.]{1}[a-z]{2,10}$", "g")
    };
  },
  methods: {
    onEnter: function onEnter() {
      var adressSignup = document.getElementById('adresse-signup');

      if (this.regexAddress.test(adressSignup.value.trim())) {
        this.regexpAdress = true;
      } else {
        this.regexpAdress = false;
      }
    },
    affichMsgConf: function affichMsgConf() {
      if (this.numberAffichMsg === 1) {
        this.numberAffichMsg = 0;
        this.boolAffichMsg = false;
      } else {
        this.numberAffichMsg = 1;
        this.boolAffichMsg = true;
      }
    },
    checkLogin: function checkLogin() {
      var signupCheck = {
        signupNom: document.querySelector('#nom-signup').value,
        signupPrenom: document.querySelector('#prenom-signup').value,
        signupEmail: document.querySelector('#email-signup').value,
        signupPassword: document.querySelector('#password-signup').value,
        signupDate: document.querySelector('#date-signup').value,
        signupVille: document.querySelector('#ville-signup').value,
        signupAdresse: document.querySelector('#adresse-signup').value
      };
      fetch('http://localhost:3000/app/signup', {
        method: "POST",
        headers: {
          'Accept': 'application/json',
          'Content-type': 'application/json'
        },
        body: JSON.stringify(signupCheck)
      }).then(function (res) {
        return res.json();
      }).catch(function () {
        document.querySelector(".mb-3").innerHTML += "<p class=\"errorColor\">Erreur serveur indisponible, veuillez r\xE9essayer plus tard !</p>";
        setTimeout(function () {
          document.querySelector('.errorColor').innerHTML = "";
        }, 1500);
      });
    }
  }
};
