export default {
  name: 'loginPage',
  data: function data() {
    return {
      mail: 'Adresse mail',
      password: 'Mot de passe',
      affichMdp: 'Aperçu du mot de passe',
      messageMdp: '',
      numberAffichMsg: false,
      regexpBoolPassword: null,
      passwordRegExp: new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]))(?=.{8,})"),
      emailRegExp: new RegExp(/^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/i)
    };
  },
  methods: {
    affichMsgConf: function affichMsgConf() {
      if (this.numberAffichMsg === false) {
        this.numberAffichMsg = true;
        document.getElementById('password-signup').setAttribute("type", "password");
      } else {
        this.numberAffichMsg = false;
        document.getElementById('password-signup').setAttribute("type", "text");
      }
    },
    onEnter: function onEnter() {
      var passwordSignup = document.getElementById('password-signup');
      if (this.passwordRegExp.test(passwordSignup.value.trim()) ? this.regexpBoolPassword = true : this.regexpBoolPassword = false) ;
    },
    checkLogin: function checkLogin() {
      var loginCheck = {
        loginEmail: document.querySelector('#email-login').value,
        loginPassword: document.querySelector('#password-login').value
      };
      fetch('http://localhost:3000/app/user', {
        method: "POST",
        headers: {
          'Accept': 'application/json',
          'Content-type': 'application/json'
        },
        body: JSON.stringify(loginCheck)
      }).then(function (res) {
        return res.json();
      }).then(function () {
        window.location.href = "espace-user.html";
      }).catch(function () {
        document.querySelector(".mb-3").innerHTML += "<p class=\"errorColor\">Erreur serveur indisponible, veuillez r\xE9essayer plus tard !</p>";
