export default {
  name: 'signupPage',
  data: function data() {
    return {
      name: 'Nom',
      firstName: 'Prénom',
      mail: 'Adresse mail',
      password: 'Mot de passe',
      birthday: 'Date de naissance',
      city: 'Ville',
      adress: 'Adresse',
      affichMdp: 'Aperçu du mot de passe',
      messageMdp: '',
      boolAffichMsg: false,
      regexpBoolName: null,
      regexpBoolFirstName: null,
      regexpBoolAdress: null,
      regexpBoolCity: null,
      regexpBoolMail: null,
      regexpBoolPassword: null,
      regexpBoolBirthday: null,
      firstNameRegExp: new RegExp("^[a-zA-Z\xE0\xE2\xE4\xE9\xE8\xEA\xEB\xEF\xEE\xF4\xF6\xF9\xFB\xFC\xE7\xB0 -]{1,}$"),
      passwordRegExp: new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*])(?=.{8,})"),
      lastNameRegExp: new RegExp("^[a-zA-Z\xE0\xE2\xE4\xE9\xE8\xEA\xEB\xEF\xEE\xF4\xF6\xF9\xFB\xFC\xE7\xB0 -]{1,}$"),
      regexAddress: new RegExp(/^[a-zA-Z0-9 ]+$/),
      cityRegExp: new RegExp("^[a-zA-Z\xE0\xE2\xE4\xE9\xE8\xEA\xEB\xEF\xEE\xF4\xF6\xF9\xFB\xFC\xE7\xB0 -]{1,}$"),
      emailRegExp: new RegExp(/^[\w-.]+@([\w-]+.)+[\w-]{2,4}$/i),
      dateBirthdayRegExp: new RegExp(/^\d{4}[\/\-](0?[1-9]|1[012])[\/\-](0?[1-9]|[12][0-9]|3[01])$/)
    };
  },
  methods: {
    onEnter: function onEnter() {
      var nameSignup = document.getElementById('nom-signup');
      var firstNameSignup = document.getElementById('prenom-signup');
      var adressSignup = document.getElementById('adresse-signup');
      var citySignup = document.getElementById('ville-signup');
      var mailSignup = document.getElementById('email-signup');
      var passwordSignup = document.getElementById('password-signup');
      var dateSignup = document.getElementById('date-signup');
      if (this.lastNameRegExp.test(nameSignup.value.trim()) ? this.regexpBoolName = true : this.regexpBoolName = false) ;
      if (this.firstNameRegExp.test(firstNameSignup.value.trim()) ? this.regexpBoolFirstName = true : this.regexpBoolFirstName = false) ;
      if (this.regexAddress.test(adressSignup.value.trim()) ? this.regexpBoolAdress = true : this.regexpBoolAdress = false) ;
      if (this.cityRegExp.test(citySignup.value.trim()) ? this.regexpBoolCity = true : this.regexpBoolCity = false) ;
      if (this.emailRegExp.test(mailSignup.value.trim()) ? this.regexpBoolMail = true : this.regexpBoolMail = false) ;
      if (this.passwordRegExp.test(passwordSignup.value.trim()) ? this.regexpBoolPassword = true : this.regexpBoolPassword = false) ;
      if (this.dateBirthdayRegExp.test(dateSignup.value.trim()) ? this.regexpBoolBirthday = true : this.regexpBoolBirthday = false) ;
    },
    affichMsgConf: function affichMsgConf() {
      if (this.numberAffichMsg === false) {
        this.numberAffichMsg = true;
        document.getElementById('password-signup').setAttribute("type", "password");
      } else {
        this.numberAffichMsg = false;
        document.getElementById('password-signup').setAttribute("type", "text");
      }
    },
    checkLogin: function checkLogin() {
      var signupCheck = {
        signupNom: document.querySelector('#nom-signup').value,
        signupPrenom: document.querySelector('#prenom-signup').value,
        signupEmail: document.querySelector('#email-signup').value,
        signupPassword: document.querySelector('#password-signup').value,
        signupDate: document.querySelector('#date-signup').value,
        signupVille: document.querySelector('#ville-signup').value,
        signupAdresse: document.querySelector('#adresse-signup').value
      };
      fetch('http://localhost:3000/app/signup', {
        method: "POST",
        headers: {
          'Accept': 'application/json',
          'Content-type': 'application/json'
        },
        body: JSON.stringify(signupCheck)
      }).then(function (res) {
        return res.json();
      }).catch(function () {
        document.querySelector(".mb-3").innerHTML += "<p class=\"errorColor\">Erreur serveur indisponible, veuillez r\xE9essayer plus tard !</p>";
        setTimeout(function () {
          document.querySelector('.errorColor').innerHTML = "";
        }, 1500);
      });
    }
  }
};
